package test;public class PointItem {	String word;	double point;	Kind kind;	double nextMultiply = 1.0;	public String getWord() {		return word;	}	public void setWord(String word) {		this.word = word;	}	public double getPoint() {		return point;	}	public void setPoint(double point) {		this.point = point;	}		public Kind getKind() {		return kind;	}	public void setKind(Kind kind) {		this.kind = kind;			}		public void updateMultiply(){		if(kind.equals(Kind.EMOTION) || kind.equals(Kind.POSITIVE) || kind.equals(kind.equals(Kind.NEGATIVE)))			nextMultiply = 1.0;					else 			nextMultiply = point;			}		public double getNextMultiply() {		return nextMultiply;	}	public void setNextMultiply(double nextMultiply) {		this.nextMultiply = nextMultiply;	}	public String toString(){		return word+":"+point+":"+kind;	}	 }